<div id="titlebar" class="single submit-page">	<div class="container">		<div class="sixteen columns">			<h2><i class="fa fa-plus-circle"></i> Add T0 Wallet</h2>		</div>	</div></div><?php if($user['logged_in']){?>	<span style="float:right;padding-right:5%;">		<a href="<?php echo site_url();?>/wallet/mytransections" class=" button">Transaction History</a>	</span><?php }?><div class="container">		<!-- Submit Page -->	<hr>	<br>	<div class="sixteen columns">		<form method="POST" action="<?php echo site_url()."/wallet/buy";?>" enctype="multipart/form-data">			<div class="submit-page">				<div class="row">					<div class="form col-sm-6">						<h5>Cost Per Coin</h5>						<span class="btn btn-warning"><?php echo $txt_currency_type['txt_meta_value']." ".$cost_per_coin['txt_meta_value'];?></span>					</div>				</div>				<!-- Title -->				<div class="row">					<div class="form col-sm-4">						<h5> No. Of Coins</h5>						<input class="search-field" type="text" name="txt_no_of_coins" id="txt_no_of_coins" placeholder="" value="0"/>					</div>				</div>				<div class="row">					<div class="form col-sm-4">						<h5> Amount</h5>						<span class="btn btn-warning"><?php echo $txt_currency_type['txt_meta_value'];?> <span id="txt_amount">0</span></span>					</div>				</div>				<div class="row">					<div class="form col-sm-4">						<button type="submit" class="button big margin-top-5">Make Payment <i class="fa fa-arrow-circle-right"></i></button>					</div>				</div>			</div>		</form>	</div>	</div><script>	$(document).ready(function(){		$("#txt_no_of_coins").keyup(function(){			var amount=Number(<?php echo $cost_per_coin['txt_meta_value']?>) * Number($(this).val());			$("#txt_amount").html(amount);		});	});</script>